select 下级名称,(ls+fls+lyfl+xg+lljz+wg) as bm01,ls,fls,lyfl,xg,lljz,wg,(ls0101+fls0101+lyfl0101+xg0101+lljz0101+wg0101) as bm0101,ls0101,fls0101,lyfl0101,xg0101,lljz0101,wg0101,(ls0102+fls0102+lyfl0102+xg0102+lljz0102+wg0102) as bm0102,
ls0102,fls0102,lyfl0102,xg0102,lljz0102,wg0102,(ls0103+fls0103+lyfl0103+xg0103+lljz0103+wg0103) as bm0103,ls0103,fls0103,lyfl0103,xg0103,lljz0103,wg0103 from view_sd_tdlyzzsx_sub_{jb}
where f_year ={yearfilter} and f_xzqhdm ='{xzqhfilter}'  order by 下级代码

select 下级名称,(ls+fls+lyfl+xg+lljz+wg) as bm01,ls,fls,lyfl,xg,lljz,wg,(ls0101+fls0101+lyfl0101+xg0101+lljz0101+wg0101) as bm0101,ls0101,fls0101,lyfl0101,xg0101,lljz0101,wg0101,(ls0102+fls0102+lyfl0102+xg0102+lljz0102+wg0102) as bm0102,
ls0102,fls0102,lyfl0102,xg0102,lljz0102,wg0102,(ls0103+fls0103+lyfl0103+xg0103+lljz0103+wg0103) as bm0103,ls0103,fls0103,lyfl0103,xg0103,lljz0103,wg0103 from view_sd_tdlyzzsx_sub_{jb}
where f_year ={yearfilter} and f_xzqhdm ='{xzqhfilter}'  order by 下级代码

select 行政区,项目数量,土地面积,f_xzqhdm,f_year from (
select 下级名称 as 行政区,count as 项目数量,tbmj as 土地面积,f_xzqhdm,f_year,row_number() OVER (ORDER BY tbmj DESC) AS rn FROM view_pzwjstdmj_sub_{jb} where f_year ={yearfilter} and f_xzqhdm ='{xzqhfilter}'
) a where rn < 6 order by rn
